import React from 'react';
import axios from 'axios';
import BreedList from './components/BreedList';
import DoggoPicture from './components/DoggoPicture';

//Main App Component
 class App extends React.Component {
   constructor(props) {
   super(props)
   this.state = {
     dogs: [],
     imageterm: 'random',
     isLoading: false,
     image: null
     }
   }

   componentDidMount(){
     this.setState( {isLoading: true })
     axios.get('https://dog.ceo/api/breeds/list/all')
     .then(json => this.setState({dogs: json.data.message, isLoading: false}))
     .catch(error => alert(error))
     }

  getDog(){
    let imageterm = this.state.imageterm;
    if (imageterm === 'random') {
      axios.get("https://dog.ceo/api/breeds/image/random")
      .then(json => this.setSate({image: json.data.message, isLoading: false}))
      .catch(error => alert(error))
    } else {
      axios.get("https://dog.ceo/api/breed/"+"{imageterm}/images")
      .then(json => this.setSate({image: json.data.message, isLoading: false}))
      .catch(error => alert(error))
    }
  }

  generateInitialPicture(){
    //this.setState( {isLoading: true })
    axios.get('https://dog.ceo/api/breeds/image/random')
    .then(json => this.setState({initialImage: json.data.message, isLoading: false}))
    .catch(error => alert(error))
    }

  render() {
    const dogs = this.state.dogs;
    const imageterm = this.state.imageterm;
    const getDog = this.getDog;

    return (
          <div className="app w3-row" >
            <h2 className="app-title w3-col l12 m12 s12">Dog Breed Selector <span role="img" aria-label="dog">🐶</span></h2>
            <DoggoPicture imageterm={imageterm}  getDog={getDog} />
            <DoggoLabel message={imageterm} />
            <BreedList dogs={dogs} imageterm={imageterm} />


          </div>
    );
  }
}

export default App;
