import React, { Component } from 'react';

import './App.css';

const API = "https://dog.ceo/api/breeds/image/";
const STARTING_QUERY = "random";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      dogBreeds: [],
      selectedBreed: 'American Terrier',
      greeting: 'Welcome, select a dog breed.'
    };
    console.log(this.state.selectedBreed);
  }

  componentDidMount() {
    fetch(API + STARTING_QUERY)
    .then(response => response.json())
    .then(data => this.setState ({ hits: data.hits }))

  }
  //loadDogList() {
    //makes API call to load doggo list
    //use const as this won't change
    //move to component folder & import?
  //}

  //selectDog(breed) {
  // function...{ breed: breed },
    // This function needs to show a random dog (start)
    // Allow user to select dogs from a list
  //}

  render() {
    return (

      <div className="App">
        <header className="App-header">
          <h1>They Are All Good Dogs</h1>
          <p>
          { this.state.selectedBreed }
          </p>
          <p>
          { this.state.greeting }
          </p>
        </header>
      </div>
      // Insert doggo image in <p>
    );
  }
}



export default App;
