import React, { Component } from 'react';
import axios from 'axios';

import './App.css';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      dogs: [],
      selectedBreed: null,
      greeting: 'Select a dog breed.',
      isLoading: true
    }; //state
  } //constructor

  getDogs() {

    axios
    // Dog CEO API call for breed list
    .get("https://dog.ceo/api/breeds/list/all")
    .then(response =>
      response.data.message.map(dogs => ({
        breedmap: `${dogs.message}`
      }))
    )
    .then(dogs => {
          this.setState({
            dogs,
            isLoading: false
          });
        })
    .catch(error => this.setState({ error, isLoading: false }));
  } //getDogs

componentWillMount() {
  this.getDogs();
}

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <h1>They Are All Good Dogs</h1>
        </header>
        <p>
        { this.state.selectedBreed }
        </p>
        <p>
        { this.state.dogs.breedmap }
        </p>
        <p>
        { this.state.greeting }
        </p>
      </div>
      // Insert doggo image in <p>
    );
  } //render
} // Component




export default App;
